"use strict";

const _require = require(`gatsby-source-filesystem`),
      createFilePath = _require.createFilePath;

exports.onCreateNode = arg => {
  const node = arg.node,
        getNode = arg.getNode;

  if (node.internal.type === `MarkdownRemark`) {
    const slug = createFilePath({
      node,
      getNode,
      basePath: `pages`
    });
    arg.actions.createNodeField({
      node,
      name: `slug`,
      value: slug
    });
  }
};